import React, { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [readings, setReadings] = useState([]);
  const [devices, setDevices] = useState([]);

  const fetchData = async () => {
    try {
      const readingsRes = await fetch('http://localhost:5001/api/readings');
      const devicesRes = await fetch('http://localhost:5001/api/devices');
      const readingsData = await readingsRes.json();
      const devicesData = await devicesRes.json();
      setReadings(readingsData);
      setDevices(devicesData);
    } catch (err) {
      console.error('Error fetching data:', err);
    }
  };

  useEffect(() => {
    fetchData();
    const interval = setInterval(fetchData, 5000); // Auto-refresh every 5 sec
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="App">
      <h1>ðŸ“¡ IoT Dashboard</h1>

      <section>
        <h2>ðŸŸ¢ Connected Devices ({devices.length})</h2>
        <ul>
          {devices.map((mac) => (
            <li key={mac}>{mac}</li>
          ))}
        </ul>
      </section>

      <section>
        <h2>ðŸ“Š Latest Sensor Readings</h2>
        <table border="1" cellPadding="8">
          <thead>
            <tr>
              <th>MAC</th>
              <th>Temperature (Â°C)</th>
              <th>Humidity (%)</th>
              <th>Voltage (V)</th>
              <th>Timestamp</th>
            </tr>
          </thead>
          <tbody>
            {readings.map((r, index) => (
              <tr key={index}>
                <td>{r.mac}</td>
                <td>{r.temperature}</td>
                <td>{r.humidity}</td>
                <td>{r.voltage}</td>
                <td>{new Date(r.timestamp).toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>
    </div>
  );
}

export default App;
